import React, { useState } from 'react';

export const useForm = (initialFieldValues: any) => {
    const [values, setValues] = useState(initialFieldValues);
    const handleInputChange = (e: any) => {
        const { name, value } = e.target;
        setValues({
            ...initialFieldValues,
            [name]: value
        })
    };

    return {
        values, 
        setValues, 
        handleInputChange
    };
};

export const Form = () => {
    return (
        <form></form>
    );
};
