import React, { useState } from 'react';

const useForm = (initialFieldValues: any) => {
    const [values, setValues] = useState(initialFieldValues);
    const handleInputChange = (e: any) => {
        const { name, value } = e.target;
        setValues({
            ...initialFieldValues,
            [name]: value
        })
    };

    return {
        values, 
        setValues, 
        handleInputChange
    };
}

export default useForm;
