import React, { useEffect, useState } from 'react';
import { Grid, InputAdornment } from '@material-ui/core';
import { useForm, Form } from '../Components/useForm';
import AccountCircle from '@material-ui/icons/AccountCircle';
import EmailIcon from '@material-ui/icons/Email';
import Controls from '../Components/Controls/Controls';

const initialFieldValues = {
    fullName: '',
    email: '',
    numBlendedLearning: 0,
    numSchedules: 0,
    numRooms: 1,
    numSetsSameDay: 0,
    numPairsDiffDay: 0,
    numSpecialSets: 0,
};

const DataForm = (): any => {
    const [values, setValues] = useState(initialFieldValues);
    // const {values, setValues } = useForm(initialFieldValues);

    useEffect(() => console.log(initialFieldValues), [values]);

    const handleInputChange = (e: any) => {
        const { name, value } = e.target;
        const temp = values;
        temp[name] = value;
        setValues(temp);
    };

    return (
        <Form>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input 
                        InputProps={{
                            startAdornment: (
                            <InputAdornment position="start">
                                <AccountCircle />
                            </InputAdornment>
                            ),
                        }}
                        required
                        type="text"
                        label="Full Name"
                        name="fullName"
                        value={values.fullName}
                        onInput={handleInputChange}
                    />
                    {/* <Controls.Input 
                        label="Blended Learning"
                        name="numBlendedLearning"
                        value={values.numBlendedLearning}
                        onInput={handleInputChange}
                    />
                    <Controls.Input
                        label="Number of Schedules"
                        name="numSchedules"
                        value={values.numSchedules}
                        onInput={handleInputChange}
                    />
                    <Controls.Input
                        label="Number of Rooms"
                        name="numRooms"
                        value={values.numRooms}
                        onInput={handleInputChange}
                    /> */}
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input 
                        InputProps={{
                            startAdornment: (
                            <InputAdornment position="start">
                                <EmailIcon />
                            </InputAdornment>
                            ),
                        }}
                        required
                        type="text"
                        label="Email"
                        name="email"
                        value={values.email}
                        onInput={handleInputChange}
                    />
                    {/* <Controls.Input
                        label="Number of sets on the same day"
                        name="numSetsSameDay"
                        value={values.numSetsSameDay}
                        onInput={handleInputChange}
                    />
                    <Controls.Input
                        label="Number of pairs on different days"
                        name="numPairsDiffDay"
                        value={values.numPairsDiffDay}
                        onInput={handleInputChange}
                    />
                    <Controls.Input
                        label="Number of special sets"
                        name="numSpecialSets"
                        value={values.numSpecialSets}
                        onInput={handleInputChange}
                    /> */}
                </Grid>
                <Grid item xs={12}>
                    <Controls.Dropzone />
                </Grid>
                <Grid item xs={12}>
                    <Controls.Button type="submit" text="Submit" />
                    <Controls.Button text="Reset" color="default" />
                </Grid>
            </Grid>
        </Form>
    )
};

